mapper.update:
UPDATE message SET is_read = #{isRead}, update_time = NOW() WHERE msg_id IN
#{msgId}
AND is_deleted = 0 -- 仅更新未删除的消息

mapper.select:
SELECT  FROM message
WHERE receiver_id = #{receiverId}
AND is_deleted = 0 -- 仅查询未删除的消息

    AND type = #{msgType.code,jdbcType=VARCHAR}

ORDER BY create_time DESC -- 按发送时间倒序（最新消息在前）
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM message
WHERE receiver_id = #{receiverId}
AND is_deleted = 0

    AND type = #{msgType.code,jdbcType=VARCHAR}

SELECT COUNT(*) FROM message
WHERE receiver_id = #{receiverId}
  AND type = #{msgType.code,jdbcType=VARCHAR}
  AND is_read = 0
  AND is_deleted = 0

SELECT COUNT(*) FROM message WHERE is_deleted = 0
AND (receiver_id = #{query.userId} OR sender_id = #{query.userId})
AND type = #{query.type.code,jdbcType=VARCHAR}
AND is_read = #{query.status.code}
AND (title LIKE CONCAT('%', #{query.keyword}, '%') OR content LIKE CONCAT('%', #{query.keyword}, '%'))

SELECT COUNT(*) FROM message WHERE is_deleted = 0
AND ((sender_id = #{query.userId} AND receiver_id = #{query.partnerId}) OR (sender_id = #{query.partnerId} AND receiver_id = #{query.userId}))
AND type = #{query.type.code,jdbcType=VARCHAR}

SELECT FROM message WHERE is_deleted = 0
AND ((sender_id = #{query.userId} AND receiver_id = #{query.partnerId}) OR (sender_id = #{query.partnerId} AND receiver_id = #{query.userId}))
AND type = #{query.type.code,jdbcType=VARCHAR}
ORDER BY create_time ASC
LIMIT #{query.offset}, #{query.limit}
