mapper.insert:
INSERT INTO message (
    receiver_id, sender_id, type, is_read, is_deleted,
    title, content, order_id, create_time, update_time
) VALUES (
             #{receiverId}, #{senderId}, #{type.code,jdbcType=VARCHAR},
             #{isRead}, #{isDeleted}, #{title}, #{content}, #{orderId},
             #{createTime}, #{updateTime}
         )

mapper.select:
SELECT  FROM message
WHERE msg_id = #{msgId}
AND is_deleted = 0 -- 仅查询未删除的消息

mapper.update:
UPDATE message SET is_read = #{isRead}, update_time = NOW() WHERE msg_id IN
#{msgId}
AND is_deleted = 0 -- 仅更新未删除的消息

mapper.select:
SELECT  FROM message
WHERE receiver_id = #{receiverId}
AND is_deleted = 0 -- 仅查询未删除的消息

    AND type = #{msgType.code,jdbcType=VARCHAR}

ORDER BY create_time DESC -- 按发送时间倒序（最新消息在前）
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM message
WHERE receiver_id = #{receiverId}
AND is_deleted = 0

    AND type = #{msgType.code,jdbcType=VARCHAR}

SELECT COUNT(*) FROM message
WHERE receiver_id = #{receiverId}
  AND type = #{msgType.code,jdbcType=VARCHAR}
  AND is_read = 0
  AND is_deleted = 0
