    product_id, buyer_id, seller_id, total_amount, quantity,
    receiver_name, address, phone_number, buyer_remark, status, pay_type,
    create_time, pay_time, ship_time, receive_time
) VALUES (
             #{productId}, #{buyerId}, #{sellerId}, #{totalAmount}, #{quantity},
             #{receiverName}, #{address}, #{phoneNumber}, #{buyerRemark},
             #{status.code,jdbcType=VARCHAR}, #{payType.code,jdbcType=VARCHAR},
             #{createTime}, #{payTime}, #{shipTime}, #{receiveTime}
         )

mapper.delete:
DELETE FROM `order`
WHERE order_id = #{orderId}
  AND status = 'PENDING_PAY' -- 仅允许删除未支付订单

mapper.select:
SELECT  FROM `order`
WHERE buyer_id = #{buyerId}

    AND status = #{status.code,jdbcType=VARCHAR}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT  FROM `order`
WHERE seller_id = #{sellerId}

    AND status = #{status.code,jdbcType=VARCHAR}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM `order`
WHERE buyer_id = #{buyerId}

    AND status = #{status.code,jdbcType=VARCHAR}

SELECT COUNT(*) FROM `order`
WHERE seller_id = #{sellerId}

    AND status = #{status.code,jdbcType=VARCHAR}

SELECT  FROM `order`



        buyer_id = #{userId}


        seller_id = #{userId}



        AND create_time >= #{startTime}


        AND create_time <= #{endTime}


ORDER BY create_time DESC
