mapper.insert:
INSERT INTO post (
title, content, user_id, like_count, post_follow_count,
is_hot, is_essence, is_top, status, create_time, update_time
) VALUES (
#{title}, #{content}, #{userId}, #{likeCount}, #{postFollowCount},
#{isHot}, #{isEssence}, #{isTop},
#{status.code,jdbcType=VARCHAR}, 
#{createTime}, #{updateTime}
)

mapper.select:
SELECT  FROM post
WHERE user_id = #{userId}
ORDER BY create_time DESC

SELECT  FROM post
WHERE status = #{status.code,jdbcType=VARCHAR} 
ORDER BY create_time DESC
LIMIT #{limit}

SELECT  FROM post
WHERE user_id = #{userId}
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT  FROM post


        AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))


        AND status = #{status.code,jdbcType=VARCHAR} 


        AND status = 'NORMAL'



    ORDER BY ${orderBy} ${direction}


    ORDER BY create_time DESC

LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM post



        AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))



        AND status = #{status.code,jdbcType=VARCHAR} 



        AND user_id = #{userId}

SELECT  FROM post



        AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))



        AND status = #{status.code,jdbcType=VARCHAR} 



        AND user_id = #{userId}


    LIMIT #{query.offset}, #{query.limit}




    ORDER BY ${query.sortField} ${query.sortDir}



    ORDER BY update_time DESC




    LIMIT #{query.offset}, #{query.pageSize}

mapper.update:
UPDATE post
SET status = #{status.code,jdbcType=VARCHAR} 
WHERE post_id = #{postID}

UPDATE post
is_essence = #{isEssence}, is_top = #{isTop},
WHERE post_id = #{postId}

UPDATE post
SET title = #{newTitle},
    content = #{newContent},
    update_time = #{updateTime}
WHERE post_id = #{postId}

UPDATE post
SET status = #{status.code,jdbcType=VARCHAR} 
WHERE post_id IN

    #{postId}
