mapper.delete:
UPDATE user_post_like
SET status = 'CANCELLED'
WHERE user_id = #{userId} AND post_id = #{postId}

mapper.select:
SELECT  FROM user_post_like
WHERE user_id = #{userId} AND post_id = #{postId}
AND status = 'ACTIVE'

mapper.update:
UPDATE user_post_like
SET status = 'CANCELLED'
WHERE post_id = #{postId}

UPDATE user_post_like
SET status = 'CANCELLED'
WHERE user_id = #{userId}
